import * as React from "react";
import { Kick } from "../engines/kick";
// import { Transport } from "tone";
// import * as Tone from "tone";
export class Instrument extends React.Component {
    private sound: Kick | null;
    private Transport: any;
    private ctx: AudioContext | null;
    private loopId!: number;
    constructor(props: any) {
        super(props);
        this.ctx = null;
        this.sound = null;
        // Tone.Transport.bpm.value = 120;
        // Tone.Transport.schedule(this.startLoop, "0");
        // Tone.Transport.loop = true;
        // Tone.Transport.loopEnd = "1m";
    }
    // public startLoop = (time: number) => {
    //     console.log("start loop", time);
    //     this.kick.trigger(time);
    //     this.kick.trigger(time + 0.5);
    //     this.kick.trigger(time + 1);
    //     this.kick.trigger(time + 1.5);
    // }
    public handleClick = async () => {
        // Tone.context.resume();
        // if (this.ctx.state === "running") {
        //     this.ctx.suspend();
        // } else if (this.ctx.state === "suspended") {
        //     this.ctx.resume();
        // }
        const Tone = await import("tone");
        this.Transport = Tone.Transport;
        this.Transport.loop = true;
        this.Transport.loopEnd = "1m";
        this.ctx = new AudioContext();
        this.sound = new Kick(this.ctx);
        this.createLoop();
        Tone.start();
        // this.ctx.resume();
    };
    createLoop = () => {
        this.Transport.clear(this.loopId);
        const loop = (time: number) => {
            this.sound!.trigger(time);
            this.sound!.trigger(time + 0.5);
            this.sound!.trigger(time + 1);
            this.sound!.trigger(time + 1.5);
        };
        this.loopId = this.Transport.schedule(loop, "0");
    };
    render() {
        return (
            <div>
                <button onClick={this.handleClick}>Instrument</button>
            </div>
        );
    }
}
